function signalGenerator (signalNro, duracion)    switch (signalNro)    case 0      Fs=50000;   % Se usa para la seï¿½al de calibracion unicamente      t = 0:1/Fs:duracion;      y = ones(size (t));      y = 5*y;      periodo = 1/10;  % 1/10Hz (se utiliza para graficar)       [t,ynorm] = NORMALIZE (t,y,max(y)/5);    case 1      bpm = 60;      amplitudQRS = 5;      duracionQRS = 100e-3;      amplitudT = 0.2;      duracionT = 180e-3;      duracionQT = 350e-3;                                                 bpmPace = 0;      amplitudPacemaker = 0;       duracionPacemaker = 0;       tiempoPaceQ = 0;      tao = 0;      secondPulseTime = 0;      [t,y,Fs] = QRST_PRO (duracion, bpm, amplitudQRS, duracionQRS, amplitudT, duracionT, duracionQT,...      bpmPace, amplitudPacemaker, duracionPacemaker, tao, tiempoPaceQ, secondPulseTime);      [t,ynorm] = NORMALIZE (t,y,max(y)/5);    case 2      Fs = 100000;         bpmPace = 80;      amplitudPacemaker = 700;       duracionPacemaker = 2e-3;       tao = 4e-3;       [t, y] = paceMaker (Fs, duracion, bpmPace, amplitudPacemaker, duracionPacemaker, tao);      [t,ynorm] = NORMALIZE (t,y,max(y)/715);       endswitch    figure                                    plot (t,ynorm);  sound (y,Fs,16);      % 16 bits  
endfunction
